document.addEventListener("DOMContentLoaded", function () {
    var img1 = "data:image/svg+xml;base64,";
    var img2 = "data:image/svg+xml;base64,";
    var img3 = "data:image/svg+xml;base64,";
    var img4 = "data:image/svg+xml;base64,";
    
    // inject sharetoteams.css
    var stylesheet = document.createElement("link");
    stylesheet.rel = "stylesheet";
    stylesheet.type = "text/css";
    stylesheet.href = "http://localhost:3474/sharetoteams.css";
    document.head.appendChild(stylesheet);


    // find the script element to query client id
    var scripts = document.getElementsByTagName("SCRIPT");
    var clientId = null;
    for (var i = 0; i < scripts.length; i++) {
        if (scripts[i].src.toLowerCase().indexOf("sharetoteams") != -1) {
            clientId = scripts[i].getAttribute("data-client-id");
            break;
        }
    }

    if (!clientId || clientId.length == 0)
        alert("data-client-id missing from script reference");
    else {
        var elements = document.getElementsByClassName("teams-share-button");
        for (var i = 0; i < elements.length; i++) {
            var payload = elements[i].getAttribute("data-card");
            payload = payload.replace(/^<\!\[CDATA\[|\]\]>$/g,'');
            var cardJson = JSON.parse(payload);

            var img = document.createElement("img");
            img.src = img4;
            img.onclick = () => {
                var popupRef = window.open(`https://richdizz.ngrok.io/?client_id=${clientId}`, "Share to Teams", "width=700,height=700,toolbar=yes");
                
                // there needs to be a better way of doing this by listening for window load
                window.setTimeout(() => {
                    popupRef.postMessage(JSON.stringify(cardJson), "*");
                }, 2000);
            };
            elements[i].appendChild(img);
        }
    }
}, false);